import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaIndustry, FaShoppingCart, FaWater, FaEuroSign, FaBoxOpen, FaMoneyBillWave, FaCalculator } from 'react-icons/fa';

export default function Dashboard() {
  const [jour, setJour] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/jour/dujour').then(res => setJour(res.data));
  }, []);

  if (!jour) return <div>Chargement...</div>;

  const depenses = jour.depenses ?? 0;
  const beneficeNet = (jour.chiffre_affaires ?? 0) - depenses;

  return (
    <div className="dashboard card">
      <h2 className="dashboard-title">Tableau de bord du jour</h2>
      <div style={{display: "flex", flexDirection: "column", gap: 18, alignItems: "center"}}>
        <div>
          <FaIndustry color="#0288d1" size={25} /> <span className="dashboard-label">Production :</span> <span className="dashboard-value">{jour.production}</span>
        </div>
        <div>
          <FaShoppingCart color="#0288d1" size={25} /> <span className="dashboard-label">Ventes :</span> <span className="dashboard-value">{jour.ventes}</span>
        </div>
        <div>
          <FaBoxOpen color="#0288d1" /> <span className="dashboard-label">Invendus :</span> <span className="dashboard-value">{jour.invendus}</span>
        </div>
        <div>
          <FaEuroSign color="#0288d1" /> <span className="dashboard-label">Chiffre d'affaires :</span> <span className="dashboard-value">{jour.chiffre_affaires} FG</span>
        </div>
        <div>
          <FaWater color="#0288d1" /> <span className="dashboard-label">Stock actuel :</span> <span className="dashboard-value">{jour.stock}</span>
        </div>
        <div>
          <FaMoneyBillWave color="#0288d1" /> <span className="dashboard-label">Dépenses :</span> <span className="dashboard-value">{depenses} FG</span>
        </div>
        <div>
          <FaCalculator color="#0288d1" /> <span className="dashboard-label">Bénéfice net :</span> <span className="dashboard-value">{beneficeNet} FG</span>
        </div>
      </div>
    </div>
  );
}
