import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaIndustry, FaShoppingCart, FaWater, FaEuroSign, FaBoxOpen } from 'react-icons/fa';

export default function Dashboard() {
  const [jour, setJour] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/jour/dujour').then(res => setJour(res.data));
  }, []);

  if (!jour) return <div>Chargement...</div>;

  return (
    <div className="dashboard card">
      <h2 style={{color: '#0288d1', marginBottom: 30}}>Tableau de bord du jour</h2>
      <div style={{display: "flex", flexDirection: "column", gap: 18, alignItems: "center"}}>
        <div style={{fontSize: '1.2em'}}><FaIndustry color="#0288d1" /> Production : <b>{jour.production}</b></div>
        <div style={{fontSize: '1.2em'}}><FaShoppingCart color="#0288d1" /> Ventes : <b>{jour.ventes}</b></div>
        <div style={{fontSize: '1.2em'}}><FaBoxOpen color="#0288d1" /> Invendus : <b>{jour.invendus}</b></div>
        <div style={{fontSize: '1.2em'}}><FaEuroSign color="#0288d1" /> Chiffre d'affaires : <b>{jour.chiffre_affaires} Fcfa</b></div>
        <div style={{fontSize: '1.2em'}}><FaWater color="#0288d1" /> Stock actuel : <b>{jour.stock}</b></div>
      </div>
    </div>
  );
}
