import React, { useState } from 'react';
import axios from 'axios';

export default function FormulaireSaisie({ onMaj }) {
  const [production, setProduction] = useState('');
  const [ventes, setVentes] = useState('');
  const [message, setMessage] = useState('');

  const handleProduction = () => {
    axios.post('http://localhost:5000/api/jour/dujour', { production: Number(production) })
      .then(() => { setMessage('Production enregistrée !'); onMaj(); });
  };

  const handleVentes = () => {
    axios.post('http://localhost:5000/api/jour/dujour', { ventes: Number(ventes) })
      .then(() => { setMessage('Ventes enregistrées !'); onMaj(); });
  };

  return (
    <div style={{margin: 20}}>
      <h3>Saisie du jour</h3>
      <div>
        <input type="number" placeholder="Production du jour" value={production} onChange={e => setProduction(e.target.value)} />
        <button onClick={handleProduction}>Ajouter production</button>
      </div>
      <div>
        <input type="number" placeholder="Ventes du jour" value={ventes} onChange={e => setVentes(e.target.value)} />
        <button onClick={handleVentes}>Ajouter ventes</button>
      </div>
      <div style={{color: "green"}}>{message}</div>
    </div>
  );
}
