import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Dashboard() {
  const [jour, setJour] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/jour/dujour').then(res => setJour(res.data));
  }, []);

  if (!jour) return <div>Chargement...</div>;

  return (
    <div className="dashboard" style={{background: "#e3f2fd", borderRadius: 10, padding: 20, margin: 20}}>
      <h2>Tableau de bord du jour</h2>
      <div>Production : <b>{jour.production}</b></div>
      <div>Ventes : <b>{jour.ventes}</b></div>
      <div>Invendus : <b>{jour.invendus}</b></div>
      <div>Chiffre d'affaires : <b>{jour.chiffre_affaires} Fcfa</b></div>
      <div>Stock actuel : <b>{jour.stock}</b></div>
    </div>
  );
}
